DROP TABLE IF EXISTS `course_offerings`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `course_offerings` (
  `offering_id` int NOT NULL AUTO_INCREMENT COMMENT '开课计划的唯一ID',
  `semester` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '学年学期 (例如 2025-2026-1)',
  `course_id` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '课程编号',
  `course_nature` enum('必修','选修','通识') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT '必修' COMMENT '课程性质',
  `student_count_estimate` int DEFAULT NULL COMMENT '预估总上课人数 (合班/分班后)',
  `start_week` int NOT NULL COMMENT '起始周',
  `end_week` int NOT NULL COMMENT '结束周',
  `week_pattern` enum('CONTINUOUS','SINGLE','DOUBLE','CUSTOM') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'CONTINUOUS' COMMENT '周次模式',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`offering_id`),
  KEY `course_id` (`course_id`),
  KEY `idx_semester_course` (`semester`,`course_id`),
  CONSTRAINT `course_offerings_ibfk_1` FOREIGN KEY (`course_id`) REFERENCES `courses` (`course_id`) ON DELETE CASCADE ON UPDATE CASCADE
